import React, { useState } from "react";
import "./POIPopup.css";

const POIPopup = ({ poi }) => {
  const imageLinks = poi.image?.split(",").map((link) => link.trim()) || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showFullDescription, setShowFullDescription] = useState(false);

  const showPrev = (e) => {
    e.stopPropagation();
    setCurrentIndex((prev) => (prev - 1 + imageLinks.length) % imageLinks.length);
  };

  const showNext = (e) => {
    e.stopPropagation();
    setCurrentIndex((prev) => (prev + 1) % imageLinks.length);
  };

  const toggleDescription = () => {
    setShowFullDescription((prev) => !prev);
  };

  return (
    <div className="relative w-full h-screen">
      <MapContainer
        center={[40.71427, -74.00597]}
        zoom={3}
        minZoom={3}
        maxZoom={15}
        scrollWheelZoom={true}
        className="w-full h-full"
        maxBounds={[
          [-85.05112878, -180],
          [85.05112878, 180],
        ]}
        maxBoundsViscosity={1}
      >
        {/* ... */}
      </MapContainer>

      {/* Formular Overlay */}
      <div className="absolute top-4 left-1/2 transform -translate-x-1/2 bg-white/90 dark:bg-gray-900/90 p-4 rounded-lg shadow-md z-[1000] flex gap-2 items-center">
        <input
          type="text"
          className="p-2 rounded-md border border-gray-300 dark:border-gray-600 dark:bg-gray-800 dark:text-white"
          placeholder="City 1"
          value={city1}
          onChange={(e) => setCity1(e.target.value)}
        />
        <input
          type="text"
          className="p-2 rounded-md border border-gray-300 dark:border-gray-600 dark:bg-gray-800 dark:text-white"
          placeholder="City 2"
          value={city2}
          onChange={(e) => setCity2(e.target.value)}
        />
        <button
          onClick={handleConnectCities}
          className="px-3 py-2 rounded-md bg-blue-500 text-white hover:bg-blue-600 transition"
        >
          Connect
        </button>

        <button
          onClick={() => navigate("/home")}
          className="ml-4 px-3 py-2 rounded-md bg-gray-300 dark:bg-gray-700 text-black dark:text-white hover:bg-gray-400 dark:hover:bg-gray-600 transition"
        >
          â¬… Home
        </button>
      </div>
    </div>
  );
};

export default POIPopup;
