import React, {useState, useEffect} from "react";
import { Link } from "react-router-dom";
import "./Header.css";

function Header({ user, setUser }) {
    
    const [scrolled, setScrolled] = useState(false); // Starea pentru scroll

    useEffect(() => {
        const storedUser = localStorage.getItem("user");

        if (storedUser) {
            setUser(JSON.parse(storedUser)); // Setăm utilizatorul
            };

        const handleScroll = () => {
            setScrolled(window.scrollY > 50); // Dacă se face scroll peste 50px, schimbă starea
            };
    
        window.addEventListener("scroll", handleScroll);
        return () => {
            window.removeEventListener("scroll", handleScroll);
            };
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        setUser(null); // Eliminăm utilizatorul din stare
    };

    



    return (
        <header className="header">
            {scrolled ? "bg-black shadow-lg" : "bg-transparent"}
            <div className="logo">Vaccation Suggestion App</div>
            <nav className="nav-links">
                <li><Link to="/home">Home</Link></li>
                <li><Link to="/vacation">See Vacations</Link></li>
                {!user ? (
                    <>
                    <li><Link to="/login">Login</Link></li>
                    <li><Link to="/signup">Sign Up</Link></li>
                    </>
                ) : (
                    <li><button onClick={handleLogout}>Logout</button></li>
                )}
            </nav>

        </header>
        
    );
}

export default Header;
