import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Hotels.css';
import LikeButton from '../components/LikeButton';

const Hotels = () => {
  const { id } = useParams();
  const [countryName, setCountryName] = useState('');
  const [cities, setCities] = useState([]);
  const [accomodations, setAccomodations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCity, setSelectedCity] = useState(null);
  const [minBudget, setMinBudget] = useState('');
  const [maxBudget, setMaxBudget] = useState('');
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:3000/countries/${id}`);
        const data = await response.json();
        setCountryName(data.name || '');
        setCities(data.cities || []);
        setAccomodations(data.accomodations || []);
        setLoading(false);
      } catch (error) {
        console.error("Error loading:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const fetchData = async () => {
    try {
      const response = await fetch(`http://localhost:3000/countries/${id}`);
      const data = await response.json();
      setCountryName(data.name || '');
      setCities(data.cities || []);
      setAccomodations(data.accomodations || []);
      setLoading(false);
    } catch (error) {
      console.error("Error loading:", error);
      setLoading(false);
    }
  };

  const refreshAccomodations = () => {
    fetchData(); // reîncarcă accomodations pentru a actualiza numărul de like-uri
  };

  // Filtrare hoteluri
  const filteredAccomodations = accomodations.filter((acc) => {
    const inCity = selectedCity ? acc.city_name === selectedCity : true;
    const inMinBudget = minBudget ? acc.price >= parseFloat(minBudget) : true;
    const inMaxBudget = maxBudget ? acc.price <= parseFloat(maxBudget) : true;
    return inCity && inMinBudget && inMaxBudget;
  });

  return (
    <div className="hotels-container">
      {/* Toggle icon button */}
      <button
        className="filter-toggle-button"
        onClick={() => setShowFilters(!showFilters)}
        title={showFilters ? "Hide filters" : "Show filters"}
      >
        {showFilters ? (
          <svg xmlns="http://www.w3.org/2000/svg" height="24" width="24" viewBox="0 0 24 24">
            <path d="M18.3 5.71L12 12l6.3 6.29-1.42 1.42L12 14.83l-6.29 6.3-1.42-1.42L10.59 12 4.29 5.71 5.71 4.29 12 10.59l6.29-6.3z" />
          </svg>
        ) : (
          <svg xmlns="http://www.w3.org/2000/svg" height="24" width="24" viewBox="0 0 24 24">
            <path d="M10 18h4v-2h-4v2zm-7-6h18v-2H3v2zm3-6v2h12V6H6z" />
          </svg>
        )}
      </button>

      {/* Filters */}
      {showFilters && (
        <aside className="filters-aside">
          <h3>Filters</h3>

          <label>
            Min Budget:
            <input
              type="number"
              value={minBudget}
              onChange={(e) => setMinBudget(e.target.value)}
              placeholder="e.g. 100"
            />
          </label>

          <label>
            Max Budget:
            <input
              type="number"
              value={maxBudget}
              onChange={(e) => setMaxBudget(e.target.value)}
              placeholder="e.g. 500"
            />
          </label>

          <label>
            City:
            <ul className="city-list">
              {cities.map((city) => (
                <li
                  key={city.id}
                  className={`city-item ${selectedCity === city.name ? 'selected' : ''}`}
                  onClick={() =>
                    setSelectedCity((prevCity) => (prevCity === city.name ? null : city.name))
                  }
                >
                  {city.name}
                </li>
              ))}
            </ul>
          </label>
        </aside>
      )}

      {/* Hotel results */}
      <h3 className="section-title">Available hotels in {countryName}</h3>

      <div className="accommodation-grid">
        {filteredAccomodations.length === 0 ? (
          <p>No hotels match your filters.</p>
        ) : (
          filteredAccomodations.map((acc) => (
            <div key={acc.id} className="accommodation-card">
              <h4>{acc.name}</h4>
              <p>Price: {acc.price}</p>
              <p>Rating: {acc.rating}</p>
              <p>City: {acc.city_name}</p>
              <p>Total Likes: {acc.likes_count}</p>
              {localStorage.getItem("token") ? (
                <LikeButton
                  accomodationId={acc.id}
                  onLikeChange={refreshAccomodations}
                />
              ) : (
                <p className="not-logged-message">Log in to like this hotel!</p>
              )}
              <button
                onClick={() => window.open(acc.link, '_blank')}
                className="external-link-button"
              >
                Visit Hotel Website 🌐
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Hotels;
