import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

import uk from "../assets/UnitedKingdom.jpg";
import france from "../assets/France.jpg";
import us from "../assets/UnitedStates.jpg";
import ChatBot from "../components/Chatbot";
import RecFav from "../components/RecFav";

function Home({ user, setUser }) {
  const navigate = useNavigate();
  const [countries, setCountries] = useState([]);
  const [slideIndex, setSlideIndex] = useState(0);
  const [topAccomodations, setTopAccomodations] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedUser = localStorage.getItem("user");
    if (token && storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);
      } catch {
        setUser(null);
      }
    } else {
      setUser(null);
    }
  }, [setUser]);

  useEffect(() => {
    fetch("http://localhost:3000/countries")
      .then((res) => res.json())
      .then((data) => setCountries(data.slice(0, 3)))
      .catch(console.error);
  }, []);

  useEffect(() => {
    fetch("http://localhost:3000/top_accomodations")
      .then((res) => res.json())
      .then((data) => setTopAccomodations(data))
      .catch(console.error);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setSlideIndex((prevIndex) => (prevIndex + 1) % countries.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [countries]);

  const images = {
    0: uk,
    1: us,
    2: france,
  };

  return (
    <div className=" min-h-screen flex flex-col items-center">
      <div className="relative w-screen h-[80vh] overflow-hidden">
        {countries.map((country, index) => (
          <div
            key={country.id}
            className={`absolute inset-0 flex justify-center items-center duration-500 ${
              index === slideIndex ? "opacity-100" : "opacity-0"
            }`}
          >
            <img
              src={images[index]}
              alt={country.name}
              className="w-full h-[50vw] object-cover"
            />
            <div className="bg-[linear-gradient(to_top,_#f0f9ff_0%,_rgba(247,228,233,0)_15%)] absolute inset-0 pointer-events-none"></div>
          </div>
        ))}
      </div>


      {user ? (
        <div className="text-center mt-6">
          <h1 className="text-4xl text-gray-800">Hello, {user.nume}!</h1>
          <p className="text-lg text-gray-700 mt-2">
            Welcome back to the Vacation Suggestion App! üåç
          </p>
          <p className="text-lg text-gray-700">We missed you! Start exploring your favorite destinations! ‚úàÔ∏è</p>
        </div>
      ) : (
        <div className=" border-4 p-6 mt-6 rounded-xl w-4/5 text-center text-gray-800">
          <h2 className="text-2xl font-bold mb-4">Welcome to Vacation Suggestion App! üéâ</h2>
          <p className="mb-3">Sign in or sign up to unlock amazing features, including:</p>
          <ul className="text-left list-none text-lg pl-0">
            <li className="mb-2">üíô Save your favorite destinations</li>
            <li className="mb-2">ü§ñ Get personalized recommendations through our chatbot</li>
            <li className="mb-2">üìÖ Enjoy exclusive travel tips and offers</li>
          </ul>
          <p className="mt-4 text-base text-gray-700">
            If you're not logged in yet, no worries! Let's find the perfect vacation for you! üå¥
          </p>
        </div>
      )}

      <div className="w-screen px-5 mt-10 text-center">
        <h2 class="mb-4 text-2xl font-extrabold text-gray-900 dark:text-white md:text-3xl lg:text-4xl">
          <span class="text-transparent bg-clip-text bg-gradient-to-r to-emerald-600 from-sky-400">Top 3 Hotels based on likes</span>
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 w-full">
          {topAccomodations.map((acc) => (
            <div key={acc.id} className="clip-custom bg-white text-black shadow-md w-full p-4 flex flex-col items-center">
              <h4 className="text-l font-semibold mb-2">{acc.name}</h4>
              <p className="font-bold">Price: {acc.price}</p>
              <p>Rating: {acc.rating}</p>
              <p>{acc.country_name}, {acc.city_name}</p>
              <p>Total Likes: {acc.likes_count}</p>
              <button
                onClick={() => window.open(acc.link, "_blank")}
                className="mt-3 px-4 py-2  text-black rounded transition"
              >
                Visit Hotel Website üåê
              </button>
            </div>
          ))}
        </div>
      </div>

      {user && <ChatBot />}
      {user && <RecFav />}
    </div>
  );
}

export default Home;
