import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";
import country1Img from "../assets/us.jpg";
import ChatBot from "../components/Chatbot";

function Home({ user, setUser }) {
  const navigate = useNavigate();
  const [countries, setCountries] = useState([]);
  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedUser = localStorage.getItem("user");
  
    if (token && storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);
      } catch (error) {
        console.error("Error parsing user data from localStorage:", error);
        // Poți lăsa user-ul null dacă parsing-ul e invalid
        setUser(null);
      }
    } else {
      // Nu există token sau user, îl lași pe user null.
      setUser(null);
    }
  }, [setUser]);
  

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch("http://localhost:3000/countries");
        if (!response.ok) throw new Error("Failed to fetch countries");
        const data = await response.json();
        console.log(data);
        setCountries(data.slice(0, 3));
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };

    fetchCountries();
  }, []);

      let slideIndex = 1;
    showSlides(slideIndex);

    // Next/previous controls
    function plusSlides(n) {
      showSlides(slideIndex += n);
    }

    // Thumbnail image controls
    function currentSlide(n) {
      showSlides(slideIndex = n);
    }

    function showSlides(n) {
      let i;
      let slides = document.getElementsByClassName("mySlides");
      let dots = document.getElementsByClassName("dot");
      if (n > slides.length) {slideIndex = 1}
      if (n < 1) {slideIndex = slides.length}
      for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
      }
      for (i = 0; i < dots.length; i++) {
        dots[i].className = dots[i].className.replace(" active", "");
      }
      slides[slideIndex-1].style.display = "block";
      dots[slideIndex-1].className += " active";
    }

  return (
    <div className="home-container">
      {user ? (
        <div>
          <h1>Hello, {user.nume}!</h1>
          <p>Welcome back to Vacation Suggestion App!</p>
          
        </div>
      ) : (
        <div>
          <h1>Hello! You can sign up.</h1>
        </div>
      )}

      <h2>Top Countries</h2>

      <div className="countries-slideshow">
        {countries.length > 0 ? (
          countries.map((country) => (
            <div className="mySlides fade" key={country.id}>
              <h3>{country.name}</h3>
              <img src={country1Img} alt="Country 1" />
            </div>
          ))
        ) : (
          <p>Loading countries...</p>
        )}
      </div>
          
      <div style="text-align:center">
        <span class="dot" onclick="currentSlide(1)"></span>
        <span class="dot" onclick="currentSlide(2)"></span>
        <span class="dot" onclick="currentSlide(3)"></span>
      </div>
      <br/>
      <br/>
      <h2>Chat with our Bot</h2>
      <ChatBot />
    </div>
  );
}

export default Home;
