import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { Settings } from "lucide-react";

function Header({ user, setUser }) {
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();
  const isHomePage = location.pathname === "/home";
  const [showPersonalize, setShowPersonalize] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 400);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  const isScrolled = scrolled || !isHomePage;
  const baseHeaderClass = `w-full fixed top-0 z-50 flex justify-between items-center transition-colors duration-300 px-8 py-4 ${
    isScrolled ? "bg-sky-50" : "bg-transparent"
  }`;

  const buttonClass = `bg-transparent border-none font-medium text-base px-4 py-2 rounded-md hover:scale-105 hover:text-sky-500 transition ${
    isScrolled ? "text-black" : "text-white"
  }`;


  return (
    <header className={baseHeaderClass}>
      <ul className="flex items-center w-full gap-8 list-none m-0">
        <li>
          <Link to="/home" className={buttonClass}>
            Home
          </Link>
        </li>
        <li>
          <Link to="/vacation" className={buttonClass}>
            See Vacations
          </Link>
        </li>

        {!user ? (
          <>
            <li>
              <Link to="/login" className={buttonClass}>
                Login
              </Link>
            </li>
            <li>
              <Link to="/signup" className={buttonClass}>
                Sign Up
              </Link>
            </li>
          </>
        ) : (
          <>
            <li>
              <Link to="/my-likes" className={buttonClass}>
                My Likes
              </Link>
            </li>
            <li>
              <button
                onClick={handleLogout}
                className={buttonClass}
              >
                Logout
              </button>
            </li>
            <li>
              <Link to="/map" className={buttonClass}>
                Points of Interest Map
              </Link>
            </li>
          </>
        )}
        
        <li className="ml-auto">
          <div
            className={`transition-all duration-500 ease-in-out rounded-lg overflow-hidden text-white ${
              showPersonalize ? "p-4 max-h-[500px] bg-gray-800" : "p-2 max-h-[48px]"
            }`}
          >
            <button
              className="ml-auto flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition"
              onClick={() => setShowPersonalize(!showPersonalize)}
            >
              <Settings className="w-5 h-5" />
            </button>

            {/* Panoul de personalizare cu fade + slide */}
            <div
              className={`transition-opacity duration-500 ease-in-out ${
                showPersonalize ? "opacity-100 mt-4" : "opacity-0 h-0 overflow-hidden"
              }`}
            >
              <div className="flex items-center gap-3">
                <label className="relative inline-block w-[50px] h-6">
                  <input
                    type="checkbox"
                    onChange={(e) => {
                      const isDark = e.target.checked;
                      document.body.classList.toggle("dark-mode", isDark);
                      localStorage.setItem("theme", isDark ? "dark" : "light");
                    }}
                    defaultChecked={localStorage.getItem("theme") === "dark"}
                    className="opacity-0 w-0 h-0"
                  />
                  <span className="absolute cursor-pointer top-0 left-0 right-0 bottom-0 bg-gray-300 rounded-full transition" />
                </label>
                <span className="text-white text-base font-medium">🌙 Dark Mode</span>
              </div>
              

              {/* Font size buttons */}
              <div className="flex justify-center gap-2 border border-gray-400 backdrop-blur px-2 py-1 rounded mt-4">
                <button
                  className="text-sm px-2 py-1 bg-gray-200 text-gray-800 hover:bg-gray-300 rounded"
                  onClick={() => {
                    document.documentElement.classList.remove("font-medium", "font-large");
                    document.documentElement.classList.add("font-small");
                  }}
                >
                  A
                </button>
                <button
                  className="text-base px-2 py-1 bg-gray-200 text-gray-800 hover:bg-gray-300 rounded"
                  onClick={() => {
                    document.documentElement.classList.remove("font-small", "font-large");
                    document.documentElement.classList.add("font-medium");
                  }}
                >
                  A
                </button>
                <button
                  className="text-lg px-2 py-1 bg-gray-200 text-gray-800 hover:bg-gray-300 rounded"
                  onClick={() => {
                    document.documentElement.classList.remove("font-small", "font-medium");
                    document.documentElement.classList.add("font-large");
                  }}
                >
                  A
                </button>
              </div>
            </div>
          </div>
        </li>


      </ul>
    </header>
  );
}

export default Header;
