import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignUp.css';


const Signup = () => {
  const [nume, setNume] = useState("");     // numele userului
  const [email, setEmail] = useState("");   // emailul userului
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // Structurăm datele de signup
    const signupData = {
      email, // email-ul
      password,// parola
      nume,// numele utilizatorului
    };

    try {
      const response = await fetch("http://localhost:3000/users", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user: signupData }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Signup successful:', data);
        navigate('/login');
      } else {
        alert('Signup failed: ' + data.errors.join(', '));
      }
    } catch (error) {
      console.error('Error sending the request:', error);
      alert('Error sending the request');
    }
  };

  return (
    <div className="signup-container">

      <div className="wavy-background">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
        <path
          fill="#0099ff"
          fillOpacity="1"
          d="M0,224L48,229.3C96,235,192,245,288,245.3C384,245,480,235,576,208C672,181,768,139,864,117.3C960,96,1056,96,1152,112C1248,128,1344,160,1392,176L1440,192L1440,0L1392,0C1344,0,1248,0,1152,0C1056,0,960,0,864,0C768,0,672,0,576,0C480,0,384,0,288,0C192,0,96,0,48,0L0,0Z"
        ></path>
      </svg>
      </div>

      <div className="signup-form-container">
        <h2>Create an Account</h2>
        <form onSubmit={handleSignup} className="signup-form">
          <input 
            type="text" 
            placeholder="Name" 
            value={nume}
            onChange={(e) => setNume(e.target.value)}
            required 
          />
          <input 
            type="email" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
          <input 
            type="password" 
            placeholder="Password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />
          <button type="submit">Sign Up</button>
          <p>
            Already have an account?{' '}
            <span className="link" onClick={() => navigate('/login')}>Login</span>
          </p>
        </form>
      </div>

    </div>
  );
};

export default Signup;
