import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Vacation() {
  const [countries, setCountries] = useState([]);
  const [filteredCountries, setFilteredCountries] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const countriesPerPage = 5;

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch("http://localhost:3000/countries");
        if (!response.ok) throw new Error("Failed to fetch countries");
        const data = await response.json();
        setCountries(data);
        setFilteredCountries(data);
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };

    fetchCountries();
  }, []);

  useEffect(() => {
    if (!searchTerm) {
      setFilteredCountries(countries);
    } else {
      const filtered = countries.filter((country) =>
        country.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredCountries(filtered);
      setCurrentPage(1); // reset la prima pagină dacă se caută ceva nou
    }
  }, [searchTerm, countries]);

  // Pagination logic
  const indexOfLastCountry = currentPage * countriesPerPage;
  const indexOfFirstCountry = indexOfLastCountry - countriesPerPage;
  const currentCountries = filteredCountries.slice(indexOfFirstCountry, indexOfLastCountry);
  const totalPages = Math.ceil(filteredCountries.length / countriesPerPage);

  return (
    <div className="flex flex-col items-center pt-20 min-h-screen ">
      <h2 className="text-5xl text-gray-800 mb-6 font-myfont font-bold">Where to?</h2>

      <div className="w-full max-w-sm min-w-[200px] mb-10">
        <div className="relative">
          <input
            type="text"
            placeholder="Search for a city or country..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full bg-white placeholder:text-slate-400 text-slate-700 text-sm border border-slate-300 rounded-md pl-3 pr-28 py-2 transition duration-300 ease-in-out focus:outline-none focus:border-slate-500 hover:border-slate-400 shadow-sm"
          />
          <button
            type="button"
            className="shadow-2xl rounded absolute top-1 right-1 flex items-center rounded bg-slate-800 py-1 px-2.5 text-sm text-white hover:bg-slate-700 transition"
          >
            <svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" className="w-4 h-4 mr-2" viewBox="0 0 24 24">
              <path
                fillRule="evenodd"
                clipRule="evenodd"
                d="M10.5 3.75a6.75 6.75 0 1 0 0 13.5 6.75 6.75 0 0 0 0-13.5ZM2.25 10.5a8.25 8.25 0 1 1 14.59 5.28l4.69 4.69a.75.75 0 1 1-1.06 1.06l-4.69-4.69A8.25 8.25 0 0 1 2.25 10.5Z"
              />
            </svg>
            Search
          </button>
        </div>
      </div>

      <div className="flex w-full flex-col items-center justify-center gap-6">
        <div className="w-full flex flex-col items-center justify-center gap-2">
          <hr className="border-1 ml-[25vw] w-[70vw] border-emerald-500 rounded-full" />
          <h2 className="font-myfont text-semibold text-4xl">Trending destinations</h2>
          <hr className="border-1 mr-[25vw] w-[70vw] border-emerald-500 rounded-full" />
        </div>

        <div className="flex flex-wrap justify-center gap-6 px-4">
          {currentCountries.length > 0 ? (
            currentCountries.map((country) => (
              <Link
                key={country.id}
                to={`/hotels/${country.id}`}
                className="bg-transparent rounded-xl w-60 p-5 text-center text-gray-800 no-underline transition-all duration-300 transform hover:-translate-y-2 hover:scale-105 animate-fadeIn"
              >
                <h3 className="text-xl font-bold mb-2">{country.name}</h3>
                <img
                  src={`images/${country.name}.png`}
                  alt={country.name}
                  className="w-full h-40 object-cover rounded-lg transition-transform duration-300 hover:scale-105"
                />
              </Link>
            ))
          ) : (
            <p className="text-lg text-gray-600">No countries found...</p>
          )}
        </div>

        {/* Pagination Controls */}
        {filteredCountries.length > countriesPerPage && (
          <div className="flex justify-center items-center mt-6 gap-4">
            <button
              onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
              className="absolute ml-20 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400 disabled:opacity-50"
            >
              ←
            </button>

            <button
              onClick={() =>
                setCurrentPage((prev) => (prev < totalPages ? prev + 1 : prev))
              }
              disabled={currentPage === totalPages}
              className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400 disabled:opacity-50"
            >
              →
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Vacation;
